import './FinanceiroAdm.css';
import '../AdmGlobal.css'
import 'bootstrap/dist/css/bootstrap.min.css'
import NavAdm from '../../../Component/NavAdm/NavAdm'
import { useEffect, useState } from 'react'
import { useNavigate } from 'react-router-dom';
import { useParams } from 'react-router-dom';

function Pagamento() {
    
    //const baseUrl = "http://34.135.105.123:8080"
    const baseUrl = "http://localhost:8080"
    const {id} = useParams()
    const navigate = useNavigate();
    const [boletoData, setboletoData] = useState({
        'empresa': '',
        'cnpj': '',
        'statusPagamento': '',
        'dataVencimento': '',
        'parcelas': '',
        'parcelaAtual': '',
        'valorTotal': '',
        'valorParcela': '',
        'dataPagamento': '',
        'formapagamento': '',
        'parcelaPagamento': ''
      })
    
      const handleClickPagamento=async (e)=>{
        try{
          fetch(`${baseUrl}/debito/NovoPagamento`, {
            method: 'PUT',
            headers:{
              'Content-Type': 'application/x-www-form-urlencoded'
            },    
            body: new URLSearchParams({
                'idBoleto': id,
                'formapagamento': postData.formapagamento,
                'parcelas': postData.parcelas
        })})
        .then(navigate("/adm"))  
        setpostData({
            'formapagamento': '',
            'parcelas': 1
        })
        }catch (err){
          console.log("erro")
        }
      }

      useEffect(()=>{
        fetch(`${baseUrl}/debito/BuscarBoletoPorId?idBoleto=${id}`, 
            {
                method:'GET',
                headers:{
                    'content-type': 'application/json',
                },
            })
            .then((resp)=> resp.json())
            .then((data)=> {
                setboletoData(data)
            })
            .catch(err => console.log(err))
    }, [id])

    const handleChanage = (e) => {
        setpostData(prev=>({...prev,[e.target.name]:e.target.value}));
      }


    return(<>

        <div className='blocoAdicional'><NavAdm></NavAdm></div>

        <div className='blocoRetornoInfo'></div>
    
    </>)
}

export default Pagamento
